import React from "react";
import PropTypes from "prop-types";
import Head from "next/head";
import { ThemeProvider } from "@material-ui/core/styles";
import CssBaseline from "@material-ui/core/CssBaseline";
import theme from "../theme";
import Layout from "../components/Layout";
import "../styles.css";

export default function MyApp(props) {
  const { Component, pageProps } = props;
  const [state, setState] = React.useState(null);

  const fetchAndSetState = (state) => {
    // busca dados de uma api aleatoria
    // aqui voce pode enviar os dados contido no state
    fetch("https://randomuser.me/api/", { qs: { field1: state.field1 } })
      .then((res) => res.json())
      .then((result) => {
        setState(result);
      });
  };

  React.useEffect(() => {
    const jssStyles = document.querySelector("#jss-server-side");
    if (jssStyles) {
      jssStyles.parentElement.removeChild(jssStyles);
    }
  }, []);

  return (
    <React.Fragment>
      <Head>
        <title>My page</title>
        <meta
          name="viewport"
          content="minimum-scale=1, initial-scale=1, width=device-width"
        />
      </Head>
      <ThemeProvider theme={theme}>
        <CssBaseline />
        <Layout setState={setState} state={state}>
          <Component {...pageProps} setState={fetchAndSetState} state={state} />
        </Layout>
      </ThemeProvider>
    </React.Fragment>
  );
}

MyApp.propTypes = {
  Component: PropTypes.elementType.isRequired,
  pageProps: PropTypes.object.isRequired,
};
