import { Box, Typography, Grid, TextField, Button } from "@material-ui/core";

function HomePage({ setState, state }) {
  const [input, setInput] = React.useState({
    field1: "",
  });

  return (
    <Grid container style={{ width: "100%" }} spacing={2}>
      <Grid item xs={6}>
        <Box height="500px" bgcolor="grey.300">
          {state ? (
            <Box> Loaded text based on: {state.field1}</Box>
          ) : (
            <Box>NO DATA SELECTED</Box>
          )}
        </Box>
        <Box mt={2} p={2} bgcolor="grey.200">
          <Grid container alignItems="center" spacing={2}>
            <Grid item xs>
              <TextField
                fullWidth
                name="field1"
                variant="outlined"
                value={input.field1}
                onChange={(event) =>
                  setInput({ ...input, field1: event.target.value })
                }
              />
            </Grid>
            <Grid item>
              <Button
                color="primary"
                variant="contained"
                onClick={() => setState(input)}
              >
                Submit
              </Button>
            </Grid>
            <Grid item>
              <Button
                color="secondary"
                variant="contained"
                onClick={() => {
                  setInput({
                    field1: "",
                  });
                  setState(null);
                }}
              >
                CLEAR
              </Button>
            </Grid>
          </Grid>
        </Box>
      </Grid>
      <Grid item xs={6}>
        <Box height="500px" bgcolor="grey.300">
          {state ? (
            <Box> Loaded text based on: {state.field1}</Box>
          ) : (
            <Box>NO DATA SELECTED</Box>
          )}
        </Box>
      </Grid>
    </Grid>
  );
}

export default HomePage;
