import {
  AppBar,
  Toolbar,
  Box,
  Typography,
  Button,
  Grid,
} from "@material-ui/core";
import Link from "next/link";

export default ({ state }) => (
  <AppBar color="secondary" position="static">
    <Toolbar>
      <Grid container alignItems="center">

        {/* Nome Selecionado */}
        <Grid item xs>
          {state ? (
            /* Caso tenha algo selecionado */
            <Typography>
              {state.results[0].name.first} {state.results[0].name.last}
            </Typography>
          ) : (
            /* Caso esteja vazio */
            <Typography>NAME</Typography>
          )}
        </Grid>

        {/* Menu de p√°ginas & links */}
        <Grid item>
          <Box color="white">
            <Link href="/">
              <Button variant="text" color="inherit">
                Home
              </Button>
            </Link>
            <Link href="/about">
              <Button variant="text" color="inherit">
                About
              </Button>
            </Link>
            <Link href="/contact">
              <Button variant="text" color="inherit">
                Contact
              </Button>
            </Link>
          </Box>
        </Grid>
      </Grid>
    </Toolbar>
  </AppBar>
);
